mapper.select:
SELECT id, pwd
FROM tbl_user
WHERE id = #{id}

mapper.update:
UPDATE tbl_user
SET pwd=#{pwd},
    email=#{email},
    phone_num=#{phoneNum},
    adds=#{adds}
WHERE user_no = #{userNo}

mapper.select:
SELECT COUNT(*)
FROM tbl_user

    WHERE email=#{email}


    WHERE phone_num=#{phoneNum}


    WHERE id=#{id}
